mapper.select:
select * from tb_news

    
        and id=#{id}
    
    
        and typeid=#{id}
    
    
        
            and title like #{titleKey}
    
    
        and comefrom=#{comefrom}
    
    
        and DATE_FORMAT(pubdatetime,'%Y%m%d')=DATE_FORMAT(#{pubdatetime},'%Y%m%d')

mapper.update:
update tb_user

    
        
            password=#{password}
        
        
            status = #{status}
        
        
            photourl = #{photourl}
        
        
            mobile = #{mobile}
        
    

where id=#{id}
